import styles from './Intro.module.css';
import useReveal from '../hooks/useReveal';

function Intro() {
    const [ref, show] = useReveal();

    return (
        <div
            ref={ref}
            className={`${styles.wrapper} ${show ? styles.show : styles.hidden}`}
        >
            <div className={styles.left}>
                <div className={styles.name}>
                    <span className={styles.yellow}>Hi,</span> I'm Asif Tanvir
                </div>

                <div className={styles.info}>
                    - 2nd Year B.Tech Student at IIEST, Shibpur
                </div>

                <div className={styles.info_main}>
                    Just a teenager who likes tinkering with stuff, hence my interest in Linux.
                    I enjoy writing C++ code, Python too. Maybe a bit of bash scripting, but I'm
                    not great at it. Oh, and ricing my desktop—I like that too. :p
                </div>

                <div className={styles.dash}></div>

                <div className={styles.socials}>
                    <div>󰌻 󰊤</div>
                    <div className={styles.smallImg}></div>
                </div>
            </div>

            <div className={styles.right}>
                <div className={styles.img}></div>
            </div>
        </div>
    );
}

export default Intro;
